import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */



const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1>{`Getting Started`}</h1>
    <hr></hr>
    <h2>{`Pre-requisites`}</h2>
    <p>{`This documentation assumes you:`}</p>
    <ul>
      <li parentName="ul">{`Are comfortable with `}<a parentName="li" {...{
          "href": "https://tinacms.org/docs"
        }}>{`TinaCMS`}</a></li>
      <li parentName="ul">{`Have a working `}<a parentName="li" {...{
          "href": "https://tinacms.org/guides"
        }}>{`Tina site`}</a>{` ready to setup with Contentful`}</li>
      <li parentName="ul">{`Have a Contentful account and have setup access tokens to your space`}</li>
    </ul>
    <h2>{`Usage`}</h2>
    <p>{`First add the package to your site, along with the necessary contentful libraries:`}</p>
    <p><inlineCode parentName="p">{`npm install react-tinacms-contentful contentful contentful-management`}</inlineCode></p>
    <p>{`Then wrap the CMS with the provider:`}</p>
    <pre><code parentName="pre" {...{}}>{`const cms = new TinaCMS();

export const Cms({children}) {
  return (
    <TinaProvider cms={cms}>
      <ContentfulProvider>
        {children}
      </ContentfulProvider>
    </TinaProvider>
  )
}
`}</code></pre>
    <p>{`Then we'll need to configure the CMS to talk to contentful. We'll pull any "secret" values out of environment variables`}<sup parentName="p" {...{
        "id": "fnref-1"
      }}><a parentName="sup" {...{
          "href": "#fn-1",
          "className": "footnote-ref"
        }}>{`1`}</a></sup>{`.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-diff"
      }}>{`+ import { ContentfulApiClient } from 'react-tinacms-contentful'
+
-const cms = new TinaCMS();
+const cms = new TinaCMS({
+  enabled: false,
+  sidebar: true,
+  apis: {
+    contentful: new ContentfulApiClient({
+      spaceId: process.env.CONTENTFUL_SPACE_ID,
+      defaultEnvironmentId: process.env.CONTENTFUL_DEFAULT_ENVIRONMENT_ID,
+      accessTokens: {
+       delivery: process.env.CONTENTFUL_DELIVERY_ACCESS_TOKEN,
+       preview: process.env.CONTENTFUL_PREVIEW_ACCESS_TOKEN
+      },
+      redirectUrl: "/"
+    })
+  }
+});

export const Cms({children}) {
  return (
    <TinaProvider cms={cms}>
      <ContentfulProvider>
        {children}
      </ContentfulProvider>
    </TinaProvider>
  )
}
`}</code></pre>
    <p>{`You'll need to create a `}<inlineCode parentName="p">{`.env`}</inlineCode>{` file to store these "secrets" in the root of the project and fill in the blanks:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-ini"
      }}>{`CONTENTFUL_SPACE_ID=
CONTENTFUL_DEFAULT_ENVIRONMENT_ID=master
CONTENTFUL_DELIVERY_ACCESS_TOKEN=
CONTENTFUL_PREVIEW_ACCESS_TOKEN=
`}</code></pre>
    <p>{`Finally, you'll need to setup you'll need to setup your editing route(s) to receive the redirect back from Contentful to finish the login workflow.
For the purposes of this demo, we'll assume it's the root route, or `}<inlineCode parentName="p">{`"/"`}</inlineCode></p>
    <pre><code parentName="pre" {...{
        "className": "language-diff"
      }}>{`+import { useContentfulAuthRedirect } from 'react-tinacms-contentful'

export const App() {
+  useContentfulAuthRedirect()
  ...
}
`}</code></pre>
    <p>{`Now you should be able to enable the CMS and login with Contentful, either by:`}</p>
    <pre><code parentName="pre" {...{}}>{`const cms = new TinaCMS({
-  enabled: false,
+  enabled: true,
`}</code></pre>
    <p>{`or:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-diff"
      }}>{`+ import { useCallback } from 'react'
+ import { useCMS } from 'tina-cms'
import { useContentfulAuthRedirect } from 'react-tinacms-contentful'

export const App() {
+  const cms = useCMS()
+  const enableCMS = useCallback(() => cms.enable())
+
  useContentfulAuthRedirect() 
  ...
+    return ( 
+    ...
+      <Button onClick={enableCMS}>Enable CMS
+    ...
+    )
  )
}
`}</code></pre>
    <div {...{
      "className": "footnotes"
    }}>
      <hr parentName="div"></hr>
      <ol parentName="div">
        <li parentName="ol" {...{
          "id": "fn-1"
        }}>{`we store "secrets" in environment variables so they can be changed dynamically at deploy time. This also keeps them out of source code, which can cause problems.`}<a parentName="li" {...{
            "href": "#fnref-1",
            "className": "footnote-backref"
          }}>{`â†©`}</a></li>
      </ol>
    </div>
    </MDXLayout>;
}

;
